import React from 'react';
import styled from "styled-components";
import {skills} from "../../data/constants";

const Container = styled.div`
  display :flex;
  flex-direction: column;
  justify-content: center;
  position : relative;
  z-index: 1;
  align-items : center;
`;

const Wrapper = styled.div`
  max-width: 1100px;
  position: relative;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items:center;
  width: 100%;
  gap: 12px;
`;

const Title = styled.div`
  font-size: 42px;
  font-weight: 600;
  text-align: center;
  margin-top: 20px;
  color: ${({theme})=> theme.text_primary};

  @media (max-width: 768px){
    margin-top: 12px;
    font-size: 32px;
  }
`;

const Desc = styled.div`
  font-size: 18px;
  max-width: 600px;
  text-align: center;
  color: ${({theme})=> theme.text_secondary};

  @media (max-width: 768px){
    font-size: 16px;
  }
`;

const SkillsContainer = styled.div`
  width: 100%;
  display: flex;
  flex-wrap: wrap;
  margin-top: 30px;
  justify-content: center;
  gap: 30px;
`;

const Skill = styled.div`
  width: 300px;
  height: 300px;
  background-color: ${({theme})=> theme.card};
  border: 0.1px solid #854CE6;
  border-radius: 16px;
  padding: 18px 36px;

  @media (max-width: 768px){
    max-width: 400px;
    padding: 10px 36px;
  }

  @media (max-width: 500px){
    max-width: 330px;
    padding: 10px 36px;
  }
`;

const SkillTitle = styled.h2`
  font-size: 28px;
  font-weight: 600;
  color : ${({theme})=> theme.text_secondary};
  margin-bottom: 20px;
  text-align : center;
`;

const Skills = () => {
  return (
    <Container id="skills">
        <Wrapper>
          <Title>Skills</Title>
          <Desc>
            Here are some of my skills on which I have been working on for the past 1 years.
          </Desc>
          <SkillsContainer>
            {skills.map((item)=>(
              <Skill>
                <SkillTitle>{item.title}</SkillTitle>
                <SkillList>
                  {item.skills.map((skill)=>(
                    <SkillItem>
                      <SkillImage src={skill.image} />
                      {skill.name}
                    </SkillItem>
                  ))}
                </SkillList>
              </Skill>
            ))

            }
          </SkillsContainer>
        </Wrapper>
    </Container>
  )
}

export default Skills;
