import React from 'react';
import styled from "styled-components";

const Container = styled.div`
  display :flex;
  flex-direction: column;
  justify-content: center;
  position : relative;
  z-index: 1;
  align-items : center;
`;

const Wrapper = styled.div`
  max-width: 1100px;
  position: relative;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items:center;
  width: 100%;
  gap: 12px;
`;

const Title = styled.div`
  font-size: 42px;
  font-weight: 600;
  text-align: center;
  margin-top: 20px;
  color: ${({theme})=> theme.text_primary};

  @media (max-width: 768px){
    margin-top: 12px;
    font-size: 32px;
  }
`;

const Desc = styled.div`
  font-size: 18px;
  max-width: 600px;
  text-align: center;
  color: ${({theme})=> theme.text_secondary};

  @media (max-width: 768px){
    font-size: 16px;
  }
`;

const ToogleGroup = styled.div`
  display: flex;
  border: 1.5px solid ${({theme})=> theme.primary};
  color: ${({theme})=> theme.primary};
  font-size: 16px;
  border-radius: 12px;
  font-weight: 500;
  margin: 22px 0;

  @media (max-width: 768px) {
    font-size: 12px;
  }
`;

const ToogleButton = styled.div`
  padding: 8px 18px;
  cursor: pointer;
  border-radius: 6px;

  ${({ active })=> 
    active && 
    `
      background-color: ${({theme})=> theme.primary +20};
    `}

    &:hover {
      background-color: ${({theme})=> theme.primary +20};
    }
`;

const Projects = () => {
  return (
    <Container id="projects">
      <Wrapper>
        <Title>Projects</Title>
        <Desc>
          I have worked on a wide range of projects. From web apps to android apps.
          Here are some of my projects.
        </Desc>
        <ToogleGroup>
          <ToogleButton>Web</ToogleButton>
        </ToogleGroup>
      </Wrapper>
    </Container>
  )
}

export default Projects;
