import React from 'react';
import styled from "styled-components";

const HeroContainer = styled.div`
  background-color : ${({theme})=>theme.card_light};
  display : flex;
  justify-content : center;
  position : relative
  padding : 80px 30px;

  @media screen and (max-width : 960px){
    padding : 66px 16px;
  }

  @media screen and (max-width : 640px){
    padding : 32px 16px;
  }

  z-index:1;
  clip-path: polygon(0 0,100% 0,100% 100%,70% 95%,0 100%);
`;

const HeroBg = styled.div`
  position : absolute;
  display : flex;
  justify-content : end;
  top : 50%;
  right : 0;
  bottom: 0;
  left : 50%;
  overflow :hidden;
  width:100%;
  height:100%;
  padding : 0 30px;
  -webkit-transform : translateX(-50%)  translateY(-50%);
  transform :  translateX(-50%)  translateY(-50%);

  @media screen and (max-width : 960px){
    padding : 0px 0px;
    justify-content: center;
  }
`

const Hero = () => {
  return (
    <div id="about">
        <HeroContainer>
          <HeroBg></HeroBg>
          <HeroInnerContainer>
            <HeroLeftContainer>

            </HeroLeftContainer>
            <HeroRightContainer>

            </HeroRightContainer>
          </HeroInnerContainer>
        </HeroContainer>
    </div>
  )
}

export default Hero;
